function SpaceContainer({ spaces}) {

  return (
    <main>
      <ul className="cards">

      {recipes.length > 0 ? (
        recipes.map((recipe) => (
          <Recipe key={recipe.id}>
            <Box>
              <h2>{recipe.title}</h2>
              <p>
                <em>Time to Complete: {recipe.minutesToComplete} minutes</em>
                &nbsp;Â·&nbsp;
                <cite>By {recipe.user.username}</cite>
              </p>
              <ReactMarkdown>{recipe.instructions}</ReactMarkdown>
            </Box>
          </Recipe>
        ))
      ) : (
        <>
          <h2>No Recipes Found</h2>
          <Button as={Link} to="/new">
            Make a New Recipe
          </Button>
        </>
      )}


        {/* <SpaceCard spaces={spaces} /> */}
        {/* {spaces.map((space) => (
          <SpaceCard
            key={space.id}
            space={space}

          />
        ))} */}
      </ul>
    </main>
  );
}

export default SpaceContainer;
